INSERT INTO video_archives VALUES(8,'01947dbf-5292-712a-a3dd-72a0284811ec','https://www.youtube.com/watch?v=O0mZapLxSyM','【アーマード・コア6 ノーダメージチャレンジ】初期機体でアーキバスバルテウス ノーダメージ撃破【Regulation Ver. 1.07.2】',replace('【この動画には、アーマード・コア6 CHAPTER5のネタバレが含まれます】\n【App Ver. 70, Regulation Ver. 1.07.2】\n【OSフルチューン】\n\nhttps://youtu.be/ST8u4DdFMEk のバルテウス戦のみ切り出した版\n\n# 攻略\n開幕はABで懐に飛び込みつつ、真下に入り込んでブレード二発\n安定のためにキャンセルで潜り込みたいが、速度的にギリギリなのでキャンセルせず突っ込んでいる\n真下〜後ろに飛び込む必要があるため、ギリギリまでタゲアシを切ったまま懐に突っ込み、ブレードを振る直前でタゲアシをONにしている\n\n内装の関係上、巡航・QBも遅めでENにも余裕が無いため、ジャンプの活用が重要になる\n特に切り返しは可能な限りジャンプで済ませて、緊急QB・ブレード・蹴りのためのENを温存したい\n特にプラズマは直線的な弾道かつ弾速も遅いため、極力ジャンプで避けたいところ\nレーザーも直線的だが弾速が速いため、タイミングや距離次第\nパルスはジャンプで弾を散らせるため、特にジャンプの効果が大きい\nミサイルはノーダメージにあたり最も危険、QBで確実に回避したい\n\nパルスとレーザーの組み合わせがなかなか厄介\n速度の関係でQBを使ってもパルスを突き放しきれないため、いざレーザーを避けるため切り返した時にギリギリで躱していたパルスがカス当たりしやすい\nそのためジャンプで弾を散らすことになるが、この時に誘導を間違えると空中でレーザーを切り返した先にパルスが残っていることがある\nパルスをジャンプで避けつつ、ノの時を書くように浮上しておくと上記の問題を回避しやすい\n\n武装やOSの条件は https://youtu.be/gGZR1_esrF4 と同じため、弾節約のためにブレードを積極的に振る必要があるのは共通\n\n# 再生リスト \nhttps://www.youtube.com/playlist?list=PLyUgu1T3L0JTQMtOrwGKufAxy4WOVA8G4\nhttps://www.youtube.com/playlist?list=PLyUgu1T3L0JSVHfT7InEs7XkNRv24xggG\n\n# チャプター\n0:00 PA破壊 1回目\n1:09 PA破壊 2回目\n1:54 PA破壊 3回目\n2:45 後半フェーズ〜PA破壊 4回目\n4:05 PA破壊 5回目\n5:14 アーキバスバルテウス撃破\n6:06 リザルト・アセン\n\n#armoredcore6 \n#アーマードコア6 #armoredcorevifiresofrubicon\n#ac6','\n',char(10)),'https://i.ytimg.com/vi/O0mZapLxSyM/hqdefault.jpg','123456789011111111','2025-01-19 08:47:58');
