INSERT INTO video_archives VALUES(12,'01948ecd-c497-75eb-a2a9-79868439fe0a','https://youtu.be/3SjFEcjGeTc','【アーマード・コア6 ツィイーチャレンジ】ユエユーでCHAPTER3「燃料基地襲撃」Aクリア【Regulation Ver. 1.07.2】',replace('【この動画には、アーマード・コア6 CHAPTER3のネタバレが含まれます】\n【App Ver. 70, Regulation Ver. 1.07.2】\n【OSフルチューン】\n\nリトル・ツィイーの搭乗機ユエユーでCHAPTER3「燃料基地襲撃」をAクリア\n\n機体速度は上がらない＋火力も出せないので、稼ぎつつ弾薬費を抑えるために素手を主体にしてみたが、Sまで届かず\nさらに1分ぐらい縮めつつほぼノーダメじゃないと厳しそう\n\n# 条件\n- リトル・ツィイーの搭乗機「ユエユー」をそのまま使用（武器・パーツ・コア拡張の追加や変更は全て禁止）\n- 各ミッションにつき1回まで、補給直後＝クリア難易度に影響しないチェックポイントのみ利用可\n    - チェックポイントを利用する場合、チェックポイント以前もユエユーをそのまま使っていることを確認するため、開始前の準備も動画に含める\n- 補給・リペア・OSチューン制限無し\n- クリアランク制限無し\n\n# 再生リスト \nhttps://www.youtube.com/playlist?list=PLyUgu1T3L0JSq5QceuueLSMRZgoJ63sUF\n\n# チャプター\n0:00 開始〜タンク4個破壊〜LC撃破\n1:25 LC撃破〜ヘリ破壊〜タンク4個破壊\n2:47 タンク2個破壊〜タンク全破壊〜エネルギープラント破壊\n4:59 VS エクドロモイ\n6:16 エクドロモイ撃破\n6:57 リザルト・アセン\n\n#armoredcore6 \n#アーマードコア6 #armoredcorevifiresofrubicon\n#ac6','\n',char(10)),'https://i.ytimg.com/vi/3SjFEcjGeTc/hqdefault.jpg','123456789011111111','2025-01-22 16:17:17');
