INSERT INTO video_archives VALUES(30,'0194c678-bcf6-755d-8df7-c4a98674d409','https://www.youtube.com/watch?v=j--a4kMFQ-k','【AC6】マニュアルエイム レッカー腕+パイルだけでアイビスシリーズCEL240撃破【ARMORED CORE VI】',replace('アーマードコア6の縛りプレイ動画です。\n\n必達目標\n・ロックオン禁止\n・使って良い攻撃はパイルバンカーのチャージ攻撃のみ(偶発的なAB体当たりは許容)\n・腕は AC-3000 WRECKER を使用\n・OSチューニングによる与ダメージ向上禁止\n・CEL240の特定の大技後の大きな隙には攻撃禁止(※1)\n・コアはFIRMEZA\n・脚部はNACHTREIHER\n・ブースト炎が青になるジェネレーターを使用\n・リペア使用禁止\n\n努力目標\n・可能な限りCEL240の攻撃動作中に攻撃する\n・できるだけ多くの大技に対する攻撃チャンスを収録する\n・パイルを外し過ぎたらリトライ\n・低難度の攻撃を外したらリトライ\n・大技を被弾したらリトライ\n\n(※1)\nオービット翼状展開→光波1回後の隙は攻撃禁止\n連剣→縦光波1回→連剣後の隙は攻撃禁止\n\n----------------\n\nプレイ時間+16.3h 撮影テイク数2+2\n(テイク2回目でだめそうだったのでまた練習してから再挑戦したので2+2表記になっています)\n\n撮影テイク数は録画しながらの挑戦回数です。\n練習時はいろんな縛りを試しているのでこの縛りの練習時間だけを抽出することはできなかったです。\n\n最後、連剣が来るのを期待して他の攻撃チャンスを見送ったけれど来なかったので諦めました。\n\n----------------\n\nIbis CEL240\nNo Lockon\nPile Bunker Only\nNo OS Tuning (with the exception of Manual Aiming)\nNo Repair\nArms WRECKER\n\n#アーマードコア6','\n',char(10)),'https://i.ytimg.com/vi/j--a4kMFQ-k/hqdefault.jpg','123456789044444444','2025-02-02 11:43:09');
INSERT INTO video_archives VALUES(31,'0194cc36-3718-744e-9ec6-1de8d37bf1c8','https://youtu.be/lHIaRURK2CY','【アーマード・コア6 ツィイーチャレンジ】ユエユーでCHAPTER3「特務機体撃破」Sクリア【Regulation Ver. 1.07.2】',replace('【この動画には、アーマード・コア6 CHAPTER3のネタバレが含まれます】\n【App Ver. 70, Regulation Ver. 1.07.2】\n【OSフルチューン】\n\nリトル・ツィイーの搭乗機ユエユーでCHAPTER3「特務機体撃破」をSクリア\n\n# 条件\n- リトル・ツィイーの搭乗機「ユエユー」をそのまま使用（武器・パーツ・コア拡張の追加や変更は全て禁止）\n- 各ミッションにつき1回まで、補給直後＝クリア難易度に影響しないチェックポイントのみ利用可\n    - チェックポイントを利用する場合、チェックポイント以前もユエユーをそのまま使っていることを確認するため、開始前の準備も動画に含める\n- 補給・リペア・OSチューン制限無し\n- クリアランク制限無し\n\n# 再生リスト \nhttps://www.youtube.com/playlist?list=PLyUgu1T3L0JSq5QceuueLSMRZgoJ63sUF\n\n# チャプター\n0:00 VS カタフラクト\n1:45 カタフラクト撃破\n2:28 リザルト・アセン\n\n#armoredcore6 \n#アーマードコア6 #armoredcorevifiresofrubicon\n#ac6','\n',char(10)),'https://i.ytimg.com/vi/lHIaRURK2CY/hqdefault.jpg','123456789011111111','2025-02-03 14:28:12');
