INSERT INTO video_archives VALUES(33,'0194eaba-f6a6-716f-80e9-69877c02e282','https://youtu.be/RafRxfIHTs4','【アーマードコア6】羽ばたくような戦いぶりチャレンジ　OS無しノーリペア【Regulation Ver. 1.07.2】',replace('【この動画には、アーマード・コア6 CHAPTER3のネタバレが含まれます】\n【App Ver. 70, Regulation Ver. 1.07.2】\n\n元ネタ： https://x.com/RLF_Officer/status/1887320215589212526\n\n# 条件\n- LC＆HCを全て起動後に「観測データ奪取」スタート地点に到着して開始\n- 盾持ちLC→高火力LC→新型HCの順番で撃破\n- OSチューン無し\n- リペア無し\n\n# 機体構成\nRIGHT ARM UNIT: WR-0777 SWEET SIXTEEN\nLEFT ARM UNIT: WR-0777 SWEET SIXTEEN\nRIGHT BACK UNIT: SB-033M MORLEY\nLEFT BACK UNIT: SB-033M MORLEY\n\nHEAD: HC-2000/BC SHADE EYE\nCORE: NACHTREIHER/40E\nARMS: AA-J-123 BASHO\nLEGS: NACHTREIHER/42E\n\nBOOSTER: ALULA/21E\nFCS: IA-C01F: OCELLUS\nGENERATOR: VP-20C\n\nEXPANSION: (Not Equipped)\n\n# 再生リスト \nhttps://www.youtube.com/playlist?list=PLyUgu1T3L0JQKuTTqBziq8_WFsT2wbtM5\n\n#armoredcore6 \n#アーマードコア6 #armoredcorevifiresofrubicon\n#ac6','\n',char(10)),'https://i.ytimg.com/vi/RafRxfIHTs4/hqdefault.jpg','123456789011111111','2025-02-09 12:41:49');
