INSERT INTO challenge_archives VALUES(4,'0194e9a1-c9dc-735f-88b8-be5659395f4d','https://x.com/RLF_Officer/status/1789099035200242147','ルビコニアンやさしみTA',replace('少し変わり種を\n#ルビコニアンやさしみTA\n周回で必ずこなす\n「移設型砲台破壊」\nそこで、目標砲台【だけ】を破壊するTAの提案です\nタイム計測は「戦闘モード起動→収支画面の報酬加算明細表示」まで\n失敗条件は\n・砲台以外の報酬加算があった場合\nのみです\nルビコニアン側に感情移入している方向けに','\n',char(10)),'123456789011111111',strftime('%s', '2025-02-09 07:34:41'));
