INSERT INTO challenge_archives VALUES(8,'0194eed3-f256-715c-a6f4-4f06436421ba','https://x.com/vflLsc0Jy5tPFbv/status/1883116920549093551','あべこべロックスミスチャレンジ',replace('#AC6\n#アーマードコア6\nミッションを説明書しましょう。\n依頼主は匿名、目標はアーキバス・バルテウスの撃破です。\nしかしただ倒すだけなら味気ない…\nそこで依頼主からは「画像のアセン固定で撃破」といった条件が課せられています。\n確かにこれならば、十分な縛りと言える、素晴らしい提案です。','\n',char(10)),'123456789011111111',strftime('%s', '2025-02-10 07:47:35'));
